import React from "react";

import Carousel from "./components/carousel";

import "./App.css";

const gallery = [
  {
    id: "first",
    title: "xPhone",
    srcname: "./src/assets/iphone.png",
    sTitle: "Lots to love. Less to spend.\nStarting at $399.",
  },
  {
    id: "second",
    title: "Tablet",
    srcname: "tablet.png",
    sTitle: "Just the right amount of everything.",
  },
  {
    id: "third",
    title: "I'm Jayce",
    srcname: "airpods.png",
    sTitle: "Nice to meet you, the following Avatar has nothing to do with me",
  },
];

const currentSlide = gallery[0];

function App() {
  return (
    <div className="App">
      <h1>{currentSlide.title}</h1>
      <p>{currentSlide.sTitle}</p>
      <img src={require("currentSlide.srcname")} alt="" />
    </div>
  );
}

export default App;
